package something;

public class Method_Return2 {
	
	
	//Return Parameterized Method.
	public int getBonus(int kaniz, int dolly){
		int totalBonus =  kaniz+dolly;
		return totalBonus;
		
		
		
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		Method_Return2 objbarca= new Method_Return2();
		System.out.println(objbarca.getBonus(900000, 800000));
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
